import React, { Component } from 'react'

import { Button }           from 'semantic-ui-react'
import { Input }            from 'semantic-ui-react'


class Captcha extends Component {
  constructor(props) {
    super(props)

    this.state = {
      value: ``
    }
  }

  componentDidMount() {
    const { captchaText= `` } = this.props
    if (this.props.onChange) this.props.onChange({ text: captchaText, value: `` })
  }

  onRefetch = () => {
    const { captchaText= `` } = this.props
    const { captchaRefetch = () => {} } = this.props
    this.setState({ value: `` })
    captchaRefetch()
    if (this.props.onChange) this.props.onChange({ text: captchaText, value: `` })
  }

  handleChange = ({ target: { name, value }}) => {
    const { captchaText= `` } = this.props
    this.setState({ [name]: value })
    if (this.props.onChange) this.props.onChange({ text: captchaText, value })
  }

  render() {
    const { captchaData = `` } = this.props

    return (
      <div>
        <span dangerouslySetInnerHTML={{
          __html: captchaData
        }}
        />

        <Button icon='refresh' type='button' floated='right' onClick={ this.onRefetch } />
        <Input
          fluid
          name='value'
          placeholder='Enter expression result'

          onChange={ this.handleChange }
        />
      </div>
    )
  }
}

import { compose }          from 'redux'

import withCaptchaMakeMath from '../../graphql/makeMath'

export default compose(
  withCaptchaMakeMath()
)(Captcha)
