import generateUpdated      from 'engine/graphql/generate/hoc/updated'

import config               from '../config'
import debug                from '../debug'
import methods              from './methods'

const subscriptionDoc =  generateUpdated({ config, debug, methods })

import withSubscriptionFactory from 'engine/graphql/generate/hoc/withSubscriptionFactory'

export default () => withSubscriptionFactory(
  (props) => props[methods.subscribeToMore],
  subscriptionDoc
)
