import React, { Component } from 'react'

import { Button }           from 'semantic-ui-react'
import { Divider }          from 'semantic-ui-react'
import { Form }             from 'semantic-ui-react'
import { Grid }             from 'semantic-ui-react'
import { Header }           from 'semantic-ui-react'
import { Image }            from 'semantic-ui-react'


// import createDebugger       from 'engine/debug'
// const debug = createDebugger(require(`./package.json`).name)

import go                   from '../../../../history/go'

import s                    from './LogoutForm.css'

class LogoutForm extends Component {
  static contextTypes = {
    // translate: PropTypes.func.isRequired,
  }

  constructor(props) {
    super(props)

    this.state = {
      values: {}
    }
  }

  onSubmit = (e) => {
    e.preventDefault()
    const { values } = this.state
    this.props.onSubmit(values)
  }

  render() {
    // const { translate } = this.context

    return (
      <Grid
        centered
        className={ s.grid }
        verticalAlign='middle'
      >
        <Grid.Column className={ s.column }>
          <Header
            as='h2'
            color='teal'
            onClick={ () => go(`/`) }
          >
            <Image src='logo1.svg' width={ 50 }/>
            Log out from account
          </Header>
          <Form size='large' onSubmit={ this.onSubmit } error>
            <Button
              color='black'
              content='Log out'
              fluid
              size='large'
            />
          </Form>
          <Divider horizontal>Go</Divider>
          <Button
            color='teal'
            content='Back'
            fluid
            size='large'
            type='button'

            onClick={ () => go(`/`) }
          />
        </Grid.Column>
      </Grid>
    )
  }
}

import withStyles           from 'isomorphic-style-loader/lib/withStyles'
import { compose }          from 'redux'

export default compose(
  withStyles(s)
)(LogoutForm)
