import gql                  from 'graphql-tag'

export default function generateQueryLogin(payload) {
  const query = `mutation sessionLogin(
  $username: String
  $password: String
) {
  sessionLogin(
    username: $username
    password: $password
  ) {
    result {
    	id
      userId
      User {
        id
        username
      }
  	}
    errors {
      code
      message
    }
  }
}`

  return gql`${ query }`
}
