import gql                  from 'graphql-tag'

export default (payload) => {
  const query = `mutation sessionSignup(
  $username: String
  $captchaValue: String
  $captchaText: String
) {
  sessionSignup(
    username: $username
    captchaValue: $captchaValue
    captchaText: $captchaText
  )
}`

  return gql`${ query }`
}
