import gql                  from 'graphql-tag'

export default (payload) => {
  const query = `mutation storyCreateWithLines(
  $name: String
  $storyLineList: [InputStoryLine]
) {
  storyCreateWithLines(
    name: $name
    storyLineList: $storyLineList    
  ) {
    result {
      id
      name
      
      authorId
      Author {
        username
      }
      
      storyLineList {
        id
        value
      }
    }
    
    errors {
      code
      message
    }
  }
}`

  return gql`${ query }`
}
