import { enable } from 'debug'
import Debug from 'debug'

try {
  if (window.localStorage.debug) {
    console.log(`Enabling debug`, window.localStorage.debug)
    enable(window.localStorage.debug)
  }
} catch (e) {
  console.error('Could not enable debug')
  console.error(e)
}

export default namespace => new Debug(`mind:${ namespace }`)
