import _                    from 'lodash'
import typeMap              from '../typeMap'

export default function generateArgString({ fieldList }) {
  return fieldList.map(({ name, type }) => {
    if (!typeMap[type]) {
      const fieldName = `${ _.lowerFirst(name) }Id`
      return `${ fieldName }: ID`
    }
    return `${ name }: ${ type }`
  }).join(`, `)
}
