import _                    from 'lodash'
import typeMap              from '../typeMap'

export default ({ fieldList }) => {
  return fieldList.map(({ name, type, typeForce }) => {
    if (typeForce) return `${ name }: ${ typeForce }`
    if (!typeMap[type]) {
      const fieldName = `${ _.lowerFirst(name) }Id`
      return `${ fieldName }: ID`
    }
    return `${ name }: ${ type }`
  }).join(`, `)
}
