import { upperFirst }       from 'lodash'
import typeMap              from '../typeMap'

export default function generateCallString({ fieldList }) {
  return fieldList.map(({ name, type }) => {
    if (!typeMap[type]) return `${ name }Id: $${ name }Id`
    return `${ name }: $${ name }`
  }).join(`\n`)
}
