import _                    from 'lodash'

export default function generateCustomResolverQueryMap({ queryMap, name, customResolverMap }) {
  return _.mapKeys(
    _.mapValues(queryMap, (doc, apiName) => {
      return (parent, payload, ctx) => customResolverMap[apiName](parent, payload, ctx)
    }),
    (doc, apiName) => {
      return `${ name }${ _.upperFirst(apiName) }`
    }
  )
}
