import { graphql }          from 'react-apollo'

import generateQueryUpdate  from 'engine/graphql/generate/query/update'

export default ({ config, debug, methods }) => (payload) => {
  return graphql(generateQueryUpdate(config), {
    props: ({ mutate }) => ({
      [methods.update]: (variables) => {
        return mutate({
          variables,
        })
      }
    })
  })
}
