import { upperFirst }       from 'lodash'

export default function generateMutationList(payload) {
  const { name } = payload

  const typeName = upperFirst(name)
  const inputTypeName = `Input${ upperFirst(name) }`
  return [
    `${ name }Create(where: ${ inputTypeName }, defaults: ${ inputTypeName }): ${ typeName }Data`,
    `${ name }Update(where: ${ inputTypeName }, defaults: ${ inputTypeName }): ${ typeName }Data`,
    `${ name }Delete(where: ${ inputTypeName }): ${ typeName }Delete`,
  ]
}
