import typeMap              from '../typeMap'

export default function generateOutputString(payload) {
  const {
    fieldList,
    // name
  } = payload

  const list = [
    ...fieldList,
    {
      name: `createdAt`,
      type: typeMap.Date,
    },
    {
      name: `updatedAt`,
      type: typeMap.Date,
    }
  ]
  return list.map(({ name, type }) => {
    if (!typeMap[type]) return `${ name }Id`
    return `${ name }`
  }).join(`\n`)
}
