import _                    from 'lodash'

export default function generateQueryList(payload) {
  const { name } = payload
  const typeName = _.upperFirst(name)
  const inputTypeName = `Input${ _.upperFirst(name) }`
  return [
    `${ name }FindAll(where: ${ inputTypeName }, limit: Int, offset: Int): ${ typeName }Data`,
  ]
}
