import { lowerFirst }       from 'lodash'
import typeMap              from '../typeMap'

export default function generateVarString({ fieldList }) {
  return fieldList.map(({ name, type }) => {
    if (!typeMap[type]) {
      const fieldName = `${ lowerFirst(name) }Id`
      return `$${ fieldName }: ID`
    }
    return `$${ name }: ${ type }`
  }).join(`, `)
}
