import React                from 'react'


import Layout               from 'app/stories/components/Layout'
import Subsystem            from 'app/stories/components/Subsystem'

import Create               from './Create'

const title = `Create`

export default {
  path: '/create',

  action({ context }) {
    return {
      title,
      component:
        <Subsystem context={ context }>
          <Layout>
            <Create title={ title } />
          </Layout>
        </Subsystem>
    }
  },
}
