import React                from 'react'


import Layout               from 'app/stories/components/Layout'
import Subsystem            from 'app/stories/components/Subsystem'

import Login                from './Login'

const title = `Login`

export default {
  path: '/login',

  action({ context }) {
    return {
      title,
      component:
        <Subsystem context={ context }>
          <Layout>
            <Login title={ title } />
          </Layout>
        </Subsystem>,
    }
  },
}
