import React                from 'react'


import Layout               from 'app/stories/components/Layout'
import Subsystem            from 'app/stories/components/Subsystem'

import Root                 from './Root'

const title = `Root`

export default {
  path: '/',

  action({ context }) {
    return {
      title,
      component:
        <Subsystem context={ context }>
          <Layout>
            <Root title={ title } />
          </Layout>
        </Subsystem>,
    }
  },
}
