import React                from 'react'


import Layout               from 'app/stories/components/Layout'
import Subsystem            from 'app/stories/components/Subsystem'

import View               from './View'

const title = `View`

export default {
  path: '/view/:id',

  action({ context, params }) {
    const { id } = params

    return {
      title,
      component:
        <Subsystem context={ context }>
          <Layout>
            <View title={ title } id={ id }/>
          </Layout>
        </Subsystem>
    }
  },
}
