export default {
  path: '/',

  children: [
    require('./Create/route').default,
    require('./Login/route').default,
    require('./Root/route').default,
    require('./View/route').default,

    require('./NotFound/route').default,
  ],

  async action({ next }) {
    const route = await next()

    route.title = `${ route.title }`
    route.description = ``

    return route
  }
}
